(14,'<span class =\"declined\">Invalid E-mail address supplied. Please check you entered it correctly.</span>',0,'Declined - Invalid Email','Interface'),
(15,'<h1>\r\n    <span style=\"font-family: Arial\">E-Mail Confirmation required</span></h1>\r\n<span style=\"font-family: Arial\">\r\nMany thanks for your interest in joining Wikipedia. \r\n    <br />\r\n    <br />\r\n    Your request for an account will be received by a team of volunteers after you confirm your E-Mail address (check your inbox or spam folder). \r\n\r\nWe wish you all the best and hope you enjoy your time on Wikipedia.\r\n    <br />\r\n    <br />\r\n\r\nRegards, \r\n    <br />\r\n    <br />\r\n\r\nThe Wikipedia Account Creation Team</span>\r\n</div>',0,'Confirmation Needed','Interface'),
(16,'The system has <strong>not</strong> submitted this request.',0,'Decline - Final','Interface'),
(17,'<font color=\"red\" size=\"4\">There is already an open request for this username. Please choose another.</font>',0,'Decline - Dup Request (Username)','Interface'),
(18,'<font color=\"red\" size=\"4\">I\'m sorry, but you have already put in a request. Please do not submit multiple requests.</font>',0,'Decline - Dup Request (Email)','Interface'),
(19,'I\'m sorry, but you are currently banned from requesting accounts using this tool. However, you can still send an email to <a href=\"mailto:accounts-enwiki-l@lists.wikimedia.org\">accounts-enwiki-l@lists.wikimedia.org</a> to request an account. \r\nThe ban on using this tool was given for the following reason: ',0,'Banned','Interface'),
(20,'<script type=\"text/javascript\" language=\"JavaScript\">\r\n<!--\r\nvar cookieName = \"dismissSiteNotice=\";\r\nvar cookiePos = document.cookie.indexOf(cookieName);\r\nvar siteNoticeID = \"%SITENOTICECOUNT%\";\r\nvar siteNoticeValue=\"%SITENOTICETEXT%\";\r\nvar cookieValue = \"\";\r\nvar msgClose = \"Dismiss\";\r\nvar msgShow=\"Show sitenotice\";\r\nvar hideTitle=\"Hide sitenotice for one week\"\r\nvar showTitle=\"Show sitenotice\"\r\nfunction updateCookie(){\r\nif (cookiePos > -1) {\r\n	cookiePos = cookiePos + cookieName.length;\r\n	var endPos = document.cookie.indexOf(\";\", cookiePos);\r\n	if (endPos > -1) {\r\n		cookieValue = document.cookie.substring(cookiePos, endPos);\r\n	} else {\r\n		cookieValue = document.cookie.substring(cookiePos);\r\n	}\r\n}\r\n\r\n}\r\nupdateCookie();\r\n	function dismissNotice() {\r\n		var date = new Date();\r\n		date.setTime(date.getTime() + 7*24*60*60*1000);\r\n		var element = document.getElementById(\'innerNotice\');\r\n		var dismissButton=document.getElementById(\'dismiss\');\r\n		if(dismissButton.childNodes[0].innerHTML==\"[\"+msgClose+\"]\"){\r\n			element.style.display = \"none\";\r\n			dismissButton.childNodes[0].innerHTML=\"[\"+msgShow+\"]\";\r\n			document.getElementById(\'sitenotice\').style.paddingBottom=\"20px\";\r\n       			 document.getElementById(\'dismiss\').title=showTitle\r\n			document.cookie = cookieName + siteNoticeID + \"; expires=\"+date.toGMTString() + \"; path=/\";\r\n		}else{\r\n			element.style.display = \"block\";\r\n			dismissButton.childNodes[0].innerHTML=\"[\"+msgClose+\"]\";\r\n     			document.getElementById(\'dismiss\').title=hideTitle\r\n			document.getElementById(\'sitenotice\').style.paddingBottom=\"8px\";\r\n			document.cookie = cookieName + \"0\" + \"; expires=\"+date.toGMTString() + \"; path=/\";\r\n		}\r\nupdateCookie();\r\n	}\r\ndocument.writeln(\'	<div id=\"sitenotice\">\');\r\ndocument.writeln(\'<span title=\"\'+showTitle+\'\" id=\"dismiss\"><a href=\"javascript:dismissNotice();\">[\'+msgShow+\']</a></span>\');\r\ndocument.writeln(\'	<div id=\"innerNotice\" style=\"display:none\">\');\r\ndocument.writeln(siteNoticeValue);\r\ndocument.writeln(\'</div>\');\r\nif (cookieValue != siteNoticeID&&cookieValue != \"0\") {\r\n	document.getElementById(\'dismiss\').childNodes[0].innerHTML=\"[\"+msgClose+\"]\";\r\n        document.getElementById(\'dismiss\').title=hideTitle\r\n	document.getElementById(\'innerNotice\').style.display=\"block\";\r\n}\r\ndocument.writeln(\'	</div>\');\r\ndocument.getElementById(\'sitenotice\').style.paddingBottom=\"20px\";\r\nif (cookieValue != siteNoticeID&&cookieValue != \"0\") {\r\n	document.getElementById(\'sitenotice\').style.paddingBottom=\"8px\";\r\n}\r\n-->\r\n</script>',5,'Sitenotice code','Internal'),
(21,'<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\" dir=\"ltr\">\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n<title>English Wikipedia Internal Account Creation Interface</title>\r\n<meta name=\"author\" content=\"Charles Melbye\"/>\r\n<meta name=\"date\" content=\"2008-04-04T09:23:14-0400\"/>\r\n<meta name=\"copyright\" content=\"\"/>\r\n<meta name=\"keywords\" content=\"\"/>\r\n<meta name=\"description\" content=\"\"/>\r\n<meta name=\"ROBOTS\" content=\"NOINDEX, NOFOLLOW\"/>\r\n<meta http-equiv=\"content-type\" content=\"text/html; charset=UTF-8\"/>\r\n<meta http-equiv=\"content-type\" content=\"application/xhtml+xml; charset=UTF-8\"/>\r\n<meta http-equiv=\"content-style-type\" content=\"text/css\"/>\r\n<meta http-equiv=\"expires\" content=\"0\"/>\r\n<style type=\"text/css\" media=\"screen\">\r\n	@import \"//toolserver.org/~acc/style.css\";\r\n</style>\r\n\r\n<script type=\"text/javascript\" src=\"script.js\"></script>\r\n</head>\r\n<body>\r\n		<div id=\"header\">\r\n			<div id=\"header-title\">\r\n				English Wikipedia Internal Account Creation Interface\r\n			</div>\r\n			<!-- Not until the code gets tweaked <div id=\"header-info\">\r\n				You are logged in as <a href=\"/settings\">charlie</a>\r\n			</div> -->\r\n		</div>\r\n		<div id=\"navigation\">\r\n			<a href=\"%TSURL%/acc.php\"><b>Account Requests</b></a>\r\n			<a href=\"%TSURL%/acc.php?action=logs\">Logs</a>\r\n                        <a href=\"%TSURL%/statistics.php?page=Users\">Users</a>\r\n                        <a href=\"%TSURL%/acc.php?action=ban\">Ban Management</a>\r\n                        <a href=\"%TSURL%/acc.php?action=messagemgmt\">Message Management</a>\r\n                        <a href=\"%TSURL%/acc.php?action=templatemgmt\">Template Management</a>\r\n%ADMINLINKSHERE%\r\n                        <a href=\"%TSURL%/search.php\">Search</a>\r\n                        <a href=\"%TSURL%/statistics.php\">Statistics</a>\r\n	                <a href=\"%TSURL%/acc.php?action=prefs\">Preferences</a>\r\n                        <a href=\"//en.wikipedia.org/wiki/Wikipedia:Request_an_account/Guide\" target=\"_blank\">Guide</a>\r\n                       <a href=\"//en.wikipedia.org/wiki/Wikipedia:Username_policy\" target=\"_blank\">U Policy</a>\r\n <a href=\"http://webchat.freenode.net/?channels=wikipedia-en-accounts\" target=\"_blank\">Chat</a>\r\n</div>',7,'Header','Internal'),
